mapper.select:
select
,
from dmi_company_info where flag_deleted = 0 and company_name = #{companyName,jdbcType=VARCHAR}

select

,

from dmi_company_info
where flag_deleted = 0

  and id = #{id,jdbcType=INTEGER}


  and LOCATE(#{companyName,jdbcType=VARCHAR}, company_name)>0


  and LOCATE(#{companyFullName,jdbcType=VARCHAR}, company_full_name)>0


  and website = #{website,jdbcType=VARCHAR}


  and LOCATE(#{contactAddress,jdbcType=VARCHAR}, contact_address)>0


  and LOCATE(#{telephone,jdbcType=VARCHAR}, telephone)>0


  and LOCATE(#{fax,jdbcType=VARCHAR}, fax)>0


  and business_license_id = #{businessLicenseId,jdbcType=INTEGER}


  and logo_url = #{logoUrl,jdbcType=VARCHAR}


  and vendor_id = #{vendorId,jdbcType=INTEGER}


  and flag_available = #{flagAvailable,jdbcType=BIT}


  and create_source = #{createSource,jdbcType=VARCHAR}


  and LOCATE(#{sourceType,jdbcType=VARCHAR}, source_type)>0


  and company_token = #{companyToken,jdbcType=VARCHAR}


  and company_token_image = #{companyTokenImage,jdbcType=VARCHAR}


  and (TO_DAYS(create_time)=TO_DAYS(#{createTime,jdbcType=TIMESTAMP}))=1


  and create_person = #{createPerson,jdbcType=INTEGER}


  and (TO_DAYS(update_time)=TO_DAYS(#{updateTime,jdbcType=TIMESTAMP}))=1


  and update_person = #{updatePerson,jdbcType=INTEGER}


  and LOCATE(#{memo,jdbcType=VARCHAR}, memo)>0


  and flag_independence = #{flagIndependence,jdbcType=BIT}

    select
    company.id, company.company_name companyName,
    company.company_full_name companyFullName, company.website, company.contact_address contactAddress,
    company.telephone, company.fax, company.business_license_id businessLicenseId,
    company.logo_url logoUrl,company.vendor_id vendorId, company.flag_deleted flagDeleted,
    company.flag_available flagAvailable,company.create_time createTime, company.create_person createPerson,
    company.update_time updateTime, company.update_person updatePerson,company.company_token companyToken,
    company.company_token_image companyTokenImage,company.flag_independence flagIndependence,users.id userId,users.username
    from dmi_company_info company
    LEFT JOIN sys_company_users users on users.company_id=company.id
    where company.flag_deleted = 0 AND users.flag_admin=1 and company.flag_independence=1
    
      and company.id = #{id,jdbcType=INTEGER}
    
    
      and LOCATE(#{companyName,jdbcType=VARCHAR}, company.company_name)>0
    
    
      and LOCATE(#{companyFullName,jdbcType=VARCHAR}, company.company_full_name)>0
    
    
      and company.website = #{website,jdbcType=VARCHAR}
    
    
      and LOCATE(#{contactAddress,jdbcType=VARCHAR}, company.contact_address)>0
    
    
      and LOCATE(#{telephone,jdbcType=VARCHAR}, company.telephone)>0
    
    
      and LOCATE(#{fax,jdbcType=VARCHAR}, company.fax)>0
    
    
      and company.business_license_id = #{businessLicenseId,jdbcType=INTEGER}
    
    
      and company.logo_url = #{logoUrl,jdbcType=VARCHAR}
    
    
      and company.vendor_id = #{vendorId,jdbcType=INTEGER}
    
    
      and company.flag_available = #{flagAvailable,jdbcType=BIT}
    
    
      and company.create_source = #{createSource,jdbcType=VARCHAR}
    
    
      and LOCATE(#{sourceType,jdbcType=VARCHAR}, company.source_type)>0
    
    
      and company.company_token = #{companyToken,jdbcType=VARCHAR}
    
    
      and company.company_token_image = #{companyTokenImage,jdbcType=VARCHAR}
    
    
      and (TO_DAYS(company.create_time)=TO_DAYS(#{createTime,jdbcType=TIMESTAMP}))=1
    
    
      and company.create_person = #{createPerson,jdbcType=INTEGER}
    
    
      and (TO_DAYS(company.update_time)=TO_DAYS(#{updateTime,jdbcType=TIMESTAMP}))=1
    
    
      and company.update_person = #{updatePerson,jdbcType=INTEGER}
    
    
      and LOCATE(#{memo,jdbcType=VARCHAR}, company.memo)>0
    
    
      and company.flag_independence = #{flagIndependence,jdbcType=BIT}
    
    GROUP BY company.id
order by company.id desc

