mapper.insert:
insert into sys_operation_message (id, user_id, message_operation, 
  message_obj_code, message_obj_id, content, 
  create_time, read_time, flag_read
  )
values (#{id,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, #{messageOperation,jdbcType=VARCHAR}, 
  #{messageObjCode,jdbcType=VARCHAR}, #{messageObjId,jdbcType=INTEGER}, #{content,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{readTime,jdbcType=TIMESTAMP}, #{flagRead,jdbcType=BIT}
  )

mapper.select:
    select company_name
    from dmi_company_info
    where id =#{companyId,jdbcType=INTEGER}

mapper.update:
update sys_operation_message
   set
     flag_read = #{flagRead,jdbcType=BIT},
     read_time = #{readTime,jdbcType=TIMESTAMP}
   where 1=1
   and  id = #{id,jdbcType=INTEGER}

 update sys_operation_message
    set
      flag_read = #{flagRead,jdbcType=BIT},
      read_time = #{readTime,jdbcType=TIMESTAMP}
    where 1=1
    and  user_id = #{userId,jdbcType=INTEGER}
    and Message_Obj_code = #{messageObjCode,jdbcType=VARCHAR}
