mapper.select:
    select
    
    from sys_console_api
    where flag_deleted = 0
    
      and id = #{id,jdbcType=INTEGER}
    
    
      and model = #{model,jdbcType=VARCHAR}
    
    
      and LOCATE(#{name,jdbcType=VARCHAR}, name)>0
    
    
      and url = #{url,jdbcType=VARCHAR}
    
    
      and (TO_DAYS(create_time)=TO_DAYS(#{createTime,jdbcType=TIMESTAMP}))=1
    
    
      and (TO_DAYS(update_time)=TO_DAYS(#{updateTime,jdbcType=TIMESTAMP}))=1
    
order by id desc
