mapper.select:
select

from sys_console_users
where flag_deleted = 0

  and LOCATE(#{nickname,jdbcType=VARCHAR}, nickname)>0


  and LOCATE(#{realName,jdbcType=VARCHAR}, real_name)>0


  and password = #{password,jdbcType=VARCHAR}


  and LOCATE(#{username,jdbcType=VARCHAR}, username)>0


  and head_portrait_url = #{headPortraitUrl,jdbcType=VARCHAR}


  and (TO_DAYS(create_time)=TO_DAYS(#{createTime,jdbcType=TIMESTAMP}))=1


  and (TO_DAYS(update_time)=TO_DAYS(#{updateTime,jdbcType=TIMESTAMP}))=1


  and act_type = #{actType,jdbcType=VARCHAR}


  and act_Object_id = #{actObjectId,jdbcType=VARCHAR}


  and flag_open_status = #{flagOpenStatus,jdbcType=BIT}


  and LOCATE(#{email,jdbcType=VARCHAR}, email)>0


  and flag_available = #{flagAvailable,jdbcType=BIT}

order by id desc

select
from sys_console_users where id in
#{id}
