mapper.select:
select chargeInvoice.update_time AS updateTime, chargeInvoice.id AS id, companyInfo.company_name AS company, bill.bill_number AS billNumber, bill.create_bill_time AS createBillTime, invoiceModel.invoice_title AS invoiceTitle, invoiceModel.taxpayer_number AS taxpayerNumber, bill.actual_fee AS actualFee, chargeInvoice.invoice_fee AS invoiceFee, chargeInvoice.invoice_date AS invoiceDate from atm_comm_charge_invoice chargeInvoice INNER JOIN dmi_invoice_model invoiceModel ON invoiceModel.id = chargeInvoice.invoice_model_id
AND invoiceModel.invoice_type = #{invoiceType,jdbcType=VARCHAR}
AND invoiceModel.invoice_account = #{invoiceAccount,jdbcType=VARCHAR}
AND invoiceModel.taxpayer_number like '%${taxpayerNumber}%'
AND invoiceModel.invoice_title like '%${invoiceTitle}%'
INNER JOIN dmi_company_info companyInfo ON chargeInvoice.company_id = companyInfo.id
AND companyInfo.id = #{companyId,jdbcType=INTEGER}
INNER JOIN atm_comm_charge_bill bill ON bill.id = chargeInvoice.bill_id
AND bill.bill_number like '%${billNumber}%'
where chargeInvoice.flag_finish_charge = 0 AND chargeInvoice.flag_deleted = 0
AND chargeInvoice.invoice_number like '%${invoiceNumber}%'
AND chargeInvoice.invoice_date >= #{invoiceDateStart,jdbcType=TIMESTAMP}
AND chargeInvoice.invoice_date <= #{invoiceDateEnd,jdbcType=TIMESTAMP}
AND chargeInvoice.invoice_fee >= #{invoiceFeeStart,jdbcType=DECIMAL}
AND chargeInvoice.invoice_fee <= #{invoiceFeeEnd,jdbcType=DECIMAL}
