mapper.select:
select

from bif_building_address
where flag_deleted = 0

    and building_id = #{buildingId,jdbcType=INTEGER}


    and flag_major = #{flagMajor,jdbcType=BIT}

mapper.update:
update bif_building_address

  flag_deleted = 1,
  update_time = #{updateTime,jdbcType=TIMESTAMP},
  update_person = #{updatePerson,jdbcType=INTEGER}

where building_id = #{buildingId,jdbcType=INTEGER}
