mapper.select:
select device.id AS id, device.device_name AS deviceName, device.brand AS brand, device.model AS model, device.update_time AS updateTime, device.create_time AS createTime, deviceTypeCatalog.catalog_name AS catalogName, device.device_sn AS deviceSn, CASE device.flag_usable WHEN 1 THEN '使用中' ELSE '已停用' END AS status from (SELECT * FROM bif_device WHERE flag_deleted = 0
AND model_id IN ( SELECT id FROM bif_device_model WHERE series_id IN ( SELECT id FROM bif_device_series WHERE brand_id = #{brandId,jdbcType=INTEGER}))
AND model_id IN ( SELECT id FROM bif_device_model WHERE series_id = #{seriesId,jdbcType=INTEGER})
and model_id = #{modelId,jdbcType=INTEGER}
and device_sn like '%${deviceSn}%'
and create_time >= #{createTimeMin,jdbcType=TIMESTAMP}
and create_time <= #{createTimeMax,jdbcType=TIMESTAMP}
and flag_usable =#{flagUsable,jdbcType=BIT}
and catalog_id in
#{parentId}
) device INNER JOIN cfg_device_type_catalog deviceTypeCatalog ON deviceTypeCatalog.id = device.catalog_id order by device.update_time desc

select IFNULL(device.device_name,'') AS deviceName, IFNULL(device.device_sn,'') AS deviceSn, IFNULL(device.brand,'') AS brand, IFNULL((SELECT s.series_name FROM bif_device_series s, bif_device_model m WHERE m.id = device.model_id AND m.series_id = s.id),'') AS series, IFNULL(device.model,'') AS model, CASE deviceTypeCatalog.level WHEN 1 THEN '' WHEN 2 THEN '' WHEN 3 then deviceTypeCatalog.catalog_name else '' end as threeLevelCatalog, CASE deviceTypeCatalog.level WHEN 1 THEN '' WHEN 2 THEN (SELECT catalog_name from cfg_device_type_catalog where id = deviceTypeCatalog.id) WHEN 3 then (SELECT catalog_name from cfg_device_type_catalog where id = deviceTypeCatalog.parent_id) else '' end as twoLevelCatalog, CASE deviceTypeCatalog.level WHEN 1 THEN '' WHEN 2 THEN (SELECT parent_id from cfg_device_type_catalog where id = deviceTypeCatalog.id) WHEN 3 then (SELECT parent_id from cfg_device_type_catalog where id = deviceTypeCatalog.parent_id) else '' end as 'fir_id', CASE deviceTypeCatalog.level WHEN 1 THEN (SELECT catalog_name from cfg_device_type_catalog where id = deviceTypeCatalog.id) WHEN 2 THEN (SELECT catalog_name from cfg_device_type_catalog where id = deviceTypeCatalog.parent_id) WHEN 3 then (select catalog_name from cfg_device_type_catalog where id = fir_id) else '' end as oneLevelCatalog, IFNULL(device.tech_param,'') AS techParam, CASE device.flag_usable WHEN 1 THEN '是' WHEN 0 THEN '否' ELSE '' END AS flagUsable, IFNULL(date_format(device.use_expire_date,'%Y-%c-%d'),'') AS useExpireDate, IFNULL(device.memo,'') AS deviceBaseInfoMemo, IFNULL(deviceCompanyRelation.asset_sn,'') AS assetSn, IFNULL((SELECT company_name FROM bif_supply_company WHERE id = deviceCompanyRelation.supply_company_id),'')AS supplyCompany, IFNULL(date_format(deviceCompanyRelation.purchase_date,'%Y-%c-%d'),'') AS purchaseDate, CONCAT(IFNULL(deviceCompanyRelation.purchase_price,0),'') AS purchasePrice, CASE device.flag_warranty WHEN 1 THEN '是' WHEN 0 THEN '否' ELSE '' END AS flagWarranty, IFNULL(date_format(device.warranty_expire_date,'%Y-%c-%d'),'') AS warrantyExpireDate, IFNULL(deviceCompanyRelation.memo,'') AS deviceAssetMemo, CASE deviceCompanyRelation.flag_assign WHEN 1 THEN '是' WHEN 0 THEN '否' ELSE '' END AS flagAssign, IFNULL(date_format(deviceAssign.assign_date,'%Y-%c-%d'),'') AS assignDate, CASE deviceAssign.assign_type WHEN 'PERSON' THEN '员工' WHEN 'STORE' THEN '门店' WHEN 'ORG' THEN '部门' ELSE '' END AS assignType, CASE deviceAssign.assign_type WHEN 'PERSON' THEN (SELECT employee_name FROM dmi_employee WHERE id = deviceAssign.assigned_employee_id) WHEN 'STORE' THEN (SELECT store_name FROM dmi_store WHERE id = deviceAssign.assigned_section_id) WHEN 'ORG' THEN (SELECT organization_name FROM dmi_organization WHERE id = deviceAssign.assigned_section_id) ELSE '' END AS assignUserName from (SELECT * FROM bif_device WHERE flag_deleted = 0
AND model_id IN ( SELECT id FROM bif_device_model WHERE series_id IN ( SELECT id FROM bif_device_series WHERE brand_id = #{brandId,jdbcType=INTEGER}))
AND model_id IN ( SELECT id FROM bif_device_model WHERE series_id = #{seriesId,jdbcType=INTEGER})
and model_id = #{modelId,jdbcType=INTEGER}
and device_sn like '%${deviceSn}%'
and catalog_id in
#{parentId}
AND flag_warranty =#{flagWarranty,jdbcType=BIT}
AND flag_usable =#{flagUsable,jdbcType=BIT} ) device INNER JOIN cfg_device_type_catalog deviceTypeCatalog ON deviceTypeCatalog.id = device.catalog_id INNER JOIN dmi_device_company_relation deviceCompanyRelation ON deviceCompanyRelation.device_id = device.id AND deviceCompanyRelation.flag_deleted = 0 AND deviceCompanyRelation.company_id = #{companyId,jdbcType=INTEGER}
AND deviceCompanyRelation.asset_sn like '%${assetSn}%'
AND deviceCompanyRelation.flag_assign =#{flagAssign,jdbcType=BIT}
and deviceCompanyRelation.purchase_date >= #{purchaseDateMin,jdbcType=TIMESTAMP}
and deviceCompanyRelation.purchase_date <= #{purchaseDateMax,jdbcType=TIMESTAMP}
INNER JOIN (dmi_device_assign deviceAssign INNER JOIN dmi_store store ON store.id = deviceAssign.assigned_section_id) ON device.id = deviceAssign.device_id AND deviceAssign.assign_type = 'STORE' AND deviceAssign.device_company_id = #{companyId,jdbcType=INTEGER} AND deviceAssign.assigned_section_id = #{typeId,jdbcType=INTEGER} AND deviceAssign.flag_deleted = 0

