_format(device.warranty_expire_date,'%Y-%c-%d'),'') AS warrantyExpireDate, IFNULL(deviceCompanyRelation.memo,'') AS deviceAssetMemo, CASE deviceCompanyRelation.flag_assign WHEN 1 THEN '是' WHEN 0 THEN '否' ELSE '' END AS flagAssign, IFNULL(date_format(deviceAssign.assign_date,'%Y-%c-%d'),'') AS assignDate, CASE deviceAssign.assign_type WHEN 'PERSON' THEN '员工' WHEN 'STORE' THEN '门店' WHEN 'ORG' THEN '部门' ELSE '' END AS assignType, CASE deviceAssign.assign_type WHEN 'PERSON' THEN (SELECT employee_name FROM dmi_employee WHERE id = deviceAssign.assigned_employee_id) WHEN 'STORE' THEN (SELECT store_name FROM dmi_store WHERE id = deviceAssign.assigned_section_id) WHEN 'ORG' THEN (SELECT organization_name FROM dmi_organization WHERE id = deviceAssign.assigned_section_id) ELSE '' END AS assignUserName from (SELECT * FROM bif_device WHERE flag_deleted = 0
AND model_id IN ( SELECT id FROM bif_device_model WHERE series_id IN ( SELECT id FROM bif_device_series WHERE brand_id = #{brandId,jdbcType=INTEGER}))
AND model_id IN ( SELECT id FROM bif_device_model WHERE series_id = #{seriesId,jdbcType=INTEGER})
and model_id = #{modelId,jdbcType=INTEGER}
and device_sn like '%${deviceSn}%'
and catalog_id in
#{parentId}
AND flag_warranty =#{flagWarranty,jdbcType=BIT}
AND flag_usable =#{flagUsable,jdbcType=BIT} ) device INNER JOIN cfg_device_type_catalog deviceTypeCatalog ON deviceTypeCatalog.id = device.catalog_id INNER JOIN dmi_device_company_relation deviceCompanyRelation ON deviceCompanyRelation.device_id = device.id AND deviceCompanyRelation.flag_deleted = 0 AND deviceCompanyRelation.company_id = #{companyId,jdbcType=INTEGER}
AND deviceCompanyRelation.asset_sn like '%${assetSn}%'
AND deviceCompanyRelation.flag_assign =#{flagAssign,jdbcType=BIT}
and deviceCompanyRelation.purchase_date >= #{purchaseDateMin,jdbcType=TIMESTAMP}
and deviceCompanyRelation.purchase_date <= #{purchaseDateMax,jdbcType=TIMESTAMP}
INNER JOIN (dmi_device_assign deviceAssign INNER JOIN dmi_organization organization ON organization.id = deviceAssign.assigned_section_id) ON device.id = deviceAssign.device_id AND deviceAssign.assign_type = 'ORG' AND deviceAssign.device_company_id = #{companyId,jdbcType=INTEGER} AND deviceAssign.assigned_section_id = #{typeId,jdbcType=INTEGER} AND deviceAssign.flag_deleted = 0

