mapper.select:
SELECT cfg.id, cfg.title, cfg.model_id modelId, cfg.catalog_name catalogName, cfg.level, cfg.parent_id parentId, cfg.order_id orderId, cfg.description FROM cfg_dispatch_detail_config AS cfg LEFT JOIN cfg_dispatch_detail_model AS model ON model.id = cfg.model_id WHERE model.id = ( SELECT MAX( id ) FROM cfg_dispatch_detail_model where 1=1
and dispatch_type = #{dispatchType,jdbcType=VARCHAR}
and model_user_type = #{modelUserType,jdbcType=VARCHAR}
and company_id = #{companyId,jdbcType=INTEGER}
) AND cfg.level = 1
