mapper.insert:
insert into cfg_fault_type_model (id, fault_define_type, version, 
  model_user_type, model_user_obj_id, create_time
  )
values (#{id,jdbcType=INTEGER}, #{faultDefineType,jdbcType=VARCHAR}, #{version,jdbcType=INTEGER}, 
  #{modelUserType,jdbcType=VARCHAR}, #{modelUserObjId,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}
  )

mapper.select:
SELECT model.id, model.fault_define_type faultDefineType, MAX(model.version) version, model.model_user_type modelUserType, model.model_user_obj_id modelUserObjId, model.create_time createTime, sys.username createPersonName FROM cfg_fault_type_model model left join sys_company_users sys on sys.id =model.create_person where 1=1
AND model.model_user_type = #{modelUserType,jdbcType=VARCHAR}
AND model.fault_define_type = #{faultDefineType,jdbcType=VARCHAR}
AND model.company_id = #{companyId,jdbcType=INTEGER}
order by model.create_time desc

    SELECT max(VERSION)
    FROM cfg_fault_type_model
    WHERE   model_user_type = #{modelUserType,jdbcType=VARCHAR}
