mapper.select:
select
,
from dmi_company_info where flag_deleted = 0 and company_name = #{companyName,jdbcType=VARCHAR}

    select
    
    ,
    
    from dmi_company_info
    where flag_deleted = 0
    
      and id = #{id,jdbcType=INTEGER}
    
    
      and LOCATE(#{companyName,jdbcType=VARCHAR}, company_name)>0
    
    
      and LOCATE(#{companyFullName,jdbcType=VARCHAR}, company_full_name)>0
    
    
      and website = #{website,jdbcType=VARCHAR}
    
    
      and LOCATE(#{contactAddress,jdbcType=VARCHAR}, contact_address)>0
    
    
      and LOCATE(#{telephone,jdbcType=VARCHAR}, telephone)>0
    
    
      and LOCATE(#{fax,jdbcType=VARCHAR}, fax)>0
    
    
      and business_license_id = #{businessLicenseId,jdbcType=INTEGER}
    
    
      and logo_url = #{logoUrl,jdbcType=VARCHAR}
    
    
      and vendor_id = #{vendorId,jdbcType=INTEGER}
    
    
      and flag_available = #{flagAvailable,jdbcType=BIT}
    
    
      and create_source = #{createSource,jdbcType=VARCHAR}
    
    
      and LOCATE(#{sourceType,jdbcType=VARCHAR}, source_type)>0
    
    
      and company_token = #{companyToken,jdbcType=VARCHAR}
    
    
      and company_token_image = #{companyTokenImage,jdbcType=VARCHAR}
    
    
      and (TO_DAYS(create_time)=TO_DAYS(#{createTime,jdbcType=TIMESTAMP}))=1
    
    
      and create_person = #{createPerson,jdbcType=INTEGER}
    
    
      and (TO_DAYS(update_time)=TO_DAYS(#{updateTime,jdbcType=TIMESTAMP}))=1
    
    
      and update_person = #{updatePerson,jdbcType=INTEGER}
    
    
      and LOCATE(#{memo,jdbcType=VARCHAR}, memo)>0
