select device.id AS id, device.device_name AS deviceName, device.brand AS brand, device.model AS model, deviceTypeCatalog.catalog_name AS catalogName, device.device_sn AS deviceSn, device.update_time AS updateTime, deviceCompanyRelation.asset_sn AS assetSn, deviceCompanyRelation.purchase_date AS purchaseDate,
deviceAssign.assign_type AS assignType, deviceAssign.assigned_section_id AS assignId, deviceAssign.assigned_employee_id AS assignEmployeeId,
'' AS assignType, 0 AS assignId, 0 AS assignEmployeeId,
CASE device.flag_usable WHEN 1 THEN '使用中' WHEN 0 THEN '已停用' ELSE '' END AS status, CASE deviceCompanyRelation.flag_assign WHEN 1 THEN '是' WHEN 0 THEN '否' ELSE '' END AS flagAssign, CASE device.flag_warranty WHEN 1 THEN '保修中' ELSE '已脱保' END AS warrantySituation from (SELECT * FROM bif_device WHERE flag_deleted = 0
AND model_id IN ( SELECT id FROM bif_device_model WHERE series_id IN ( SELECT id FROM bif_device_series WHERE brand_id = #{brandId,jdbcType=INTEGER}))
AND model_id IN ( SELECT id FROM bif_device_model WHERE series_id = #{seriesId,jdbcType=INTEGER})
and model_id = #{modelId,jdbcType=INTEGER}
and device_sn like '%${deviceSn}%'
and catalog_id in
#{parentId}
AND flag_warranty =#{flagWarranty,jdbcType=BIT}
AND flag_usable =#{flagUsable,jdbcType=BIT} ) device INNER JOIN cfg_device_type_catalog deviceTypeCatalog ON deviceTypeCatalog.id = device.catalog_id INNER JOIN dmi_device_company_relation deviceCompanyRelation ON deviceCompanyRelation.device_id = device.id AND deviceCompanyRelation.flag_deleted = 0 AND deviceCompanyRelation.company_id = #{companyId,jdbcType=INTEGER}
AND deviceCompanyRelation.flag_assign = #{flagAssign,jdbcType=BIT}
AND deviceCompanyRelation.asset_sn like '%${assetSn}%'
AND deviceCompanyRelation.flag_assign =#{flagAssign,jdbcType=BIT}
and deviceCompanyRelation.purchase_date >= #{purchaseDateMin,jdbcType=TIMESTAMP}
and deviceCompanyRelation.purchase_date <= #{purchaseDateMax,jdbcType=TIMESTAMP}
INNER JOIN dmi_device_assign deviceAssign ON device.id = deviceAssign.device_id
LEFT JOIN dmi_device_assign deviceAssign ON device.id = deviceAssign.device_id AND deviceAssign.flag_deleted = 0

select device.id AS id, device.device_name AS deviceName, device.brand AS brand, device.model AS model, deviceTypeCatalog.catalog_name AS catalogName, device.device_sn AS deviceSn, device.update_time AS updateTime, deviceCompanyRelation.asset_sn AS assetSn, deviceCompanyRelation.purchase_date AS purchaseDate, 'STORE' AS assignType, store.store_name AS assignName, CASE device.flag_usable WHEN 1 THEN '使用中' ELSE '已停用' END AS status, CASE deviceCompanyRelation.flag_assign WHEN 1 THEN '是' WHEN 0 THEN '否' ELSE '' END AS flagAssign, CASE device.flag_warranty WHEN 1 THEN '保修中' ELSE '已脱保' END AS warrantySituation from (SELECT * FROM bif_device WHERE flag_deleted = 0
AND model_id IN ( SELECT id FROM bif_device_model WHERE series_id IN ( SELECT id FROM bif_device_series WHERE brand_id = #{brandId,jdbcType=INTEGER}))
AND model_id IN ( SELECT id FROM bif_device_model WHERE series_id = #{seriesId,jdbcType=INTEGER})
and model_id = #{modelId,jdbcType=INTEGER}
and device_sn like '%${deviceSn}%'
and catalog_id in
#{parentId}
AND flag_warranty =#{flagWarranty,jdbcType=BIT}
AND flag_usable =#{flagUsable,jdbcType=BIT} ) device INNER JOIN cfg_device_type_catalog deviceTypeCatalog ON deviceTypeCatalog.id = device.catalog_id INNER JOIN dmi_device_company_relation deviceCompanyRelation ON deviceCompanyRelation.device_id = device.id AND deviceCompanyRelation.flag_deleted = 0 AND deviceCompanyRelation.company_id = #{companyId,jdbcType=INTEGER}
AND deviceCompanyRelation.asset_sn like '%${assetSn}%'
AND deviceCompanyRelation.flag_assign =#{flagAssign,jdbcType=BIT}
and deviceCompanyRelation.purchase_date >= #{purchaseDateMin,jdbcType=TIMESTAMP}
and deviceCompanyRelation.purchase_date <= #{purchaseDateMax,jdbcType=TIMESTAMP}
INNER JOIN (dmi_device_assign deviceAssign INNER JOIN dmi_store store ON store.id = deviceAssign.assigned_section_id) ON device.id = deviceAssign.device_id AND deviceAssign.assign_type = 'STORE' AND deviceAssign.device_company_id = #{companyId,jdbcType=INTEGER} AND deviceAssign.assigned_section_id = #{typeId,jdbcType=INTEGER} AND deviceAssign.flag_deleted = 0

