mapper.update:
update sys_company_relation
set empower_company_id = #{empowerCompanyId,jdbcType=INTEGER},
  authorized_company_id = #{authorizedCompanyId,jdbcType=INTEGER},
  create_time = #{createTime,jdbcType=TIMESTAMP},
  expire_date = #{expireDate,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.delete:
delete from sys_company_relation
where empower_company_id = #{empowerCompanyId,jdbcType=INTEGER}
and authorized_company_id = #{authorizedCompanyId,jdbcType=INTEGER}
