mapper.select:
SELECT from pjt_project WHERE flag_deleted = 0 AND flag_available = 1 AND company_id = #{id}
AND belong_object_type="VENDOR"
and id not in(SELECT project_id from cfg_dispatch_route WHERE 1=1 /*flag_default = 0*/
and project_id!=(SELECT CASE WHEN COUNT(1) = 0 THEN 0 ELSE id END id from pjt_project WHERE flag_deleted = 0 AND flag_available = 1 AND company_id = #{id} AND id = #{projectId}
AND belong_object_type="VENDOR"
)
)
