mapper.select:
select

from sys_company_role
where 1=1

  AND id = #{id,jdbcType=INTEGER}


  and company_id = #{companyId,jdbcType=INTEGER}


  and LOCATE(#{roleName}, role_name)>0


  and (TO_DAYS(create_time)=TO_DAYS(#{createTime,jdbcType=TIMESTAMP}))=1


  and create_person = #{createPerson,jdbcType=INTEGER}


  and (TO_DAYS(update_time)=TO_DAYS(#{updateTime,jdbcType=TIMESTAMP}))=1


  and update_person = #{updatePerson,jdbcType=INTEGER}


  and flag_deleted = #{flagDeleted,jdbcType=BIT}
