mapper.select:
select

from sys_company_users
where 1=1

  and nickname = #{nickname,jdbcType=VARCHAR}


  and password = #{password,jdbcType=VARCHAR}


  and username = #{username,jdbcType=VARCHAR}


  and head_portrait_url = #{headPortraitUrl,jdbcType=VARCHAR}


  and create_time = #{createTime,jdbcType=TIMESTAMP}


  and update_time = #{updateTime,jdbcType=TIMESTAMP}


  and flag_deleted = #{flagDeleted,jdbcType=BIT}


  and act_type = #{actType,jdbcType=VARCHAR}


  and act_Object_id = #{actObjectId,jdbcType=INTEGER}


  and flag_open_status = #{flagOpenStatus,jdbcType=BIT}


  and email = #{email,jdbcType=VARCHAR}


  and flag_available = #{flagAvailable,jdbcType=BIT}


  and company_id = #{companyId,jdbcType=INTEGER}

select

from sys_company_users
where flag_deleted = 0

  and nickname = #{nickname,jdbcType=VARCHAR}


  and password = #{password,jdbcType=VARCHAR}


  and username = #{username,jdbcType=VARCHAR}


  and head_portrait_url = #{headPortraitUrl,jdbcType=VARCHAR}


  and create_time = #{createTime,jdbcType=TIMESTAMP}


  and update_time = #{updateTime,jdbcType=TIMESTAMP}


  and act_type = #{actType,jdbcType=VARCHAR}


  and act_Object_id = #{actObjectId,jdbcType=INTEGER}


  and flag_open_status = #{flagOpenStatus,jdbcType=BIT}


  and email = #{email,jdbcType=VARCHAR}


  and flag_available = #{flagAvailable,jdbcType=BIT}


  and company_id = #{companyId,jdbcType=INTEGER}

order by create_time desc

    SELECT  vendor_name
    FROM  dmi_intergration_vendors
    where id = #{vendorId,jdbcType=INTEGER}
