mapper.select:
select users.id, users.nickname,users.real_name realName, users.password, users.username, users.head_portrait_url headPortraitUrl, users.create_time createTime, users.update_time updateTime, users.flag_deleted flagDeleted, users.act_type actType, users.act_Object_id actObjectId, users.flag_open_status flagOpenStatus, users.email, users.flag_available flagAvailable,users.mobile,GROUP_CONCAT(role.role_name) roleName from sys_console_users users LEFT JOIN sys_console_user_role_relation relation ON relation.user_id=users.id LEFT JOIN sys_console_role role ON role.id=relation.role_id where users.flag_deleted = 0
and LOCATE(#{nickname,jdbcType=VARCHAR}, users.nickname)>0
and LOCATE(#{mobile,jdbcType=VARCHAR}, users.mobile)>0
and LOCATE(#{roleName,jdbcType=VARCHAR}, role.role_name)>0
and LOCATE(#{realName,jdbcType=VARCHAR}, users.real_name)>0
and users.password = #{password,jdbcType=VARCHAR}
and LOCATE(#{username,jdbcType=VARCHAR}, users.username)>0
and users.head_portrait_url = #{headPortraitUrl,jdbcType=VARCHAR}
and (TO_DAYS(users.create_time)=TO_DAYS(#{createTime,jdbcType=TIMESTAMP}))=1
and (TO_DAYS(users.update_time)=TO_DAYS(#{updateTime,jdbcType=TIMESTAMP}))=1
and users.act_type = #{actType,jdbcType=VARCHAR}
and users.act_Object_id = #{actObjectId,jdbcType=VARCHAR}
and users.flag_open_status = #{flagOpenStatus,jdbcType=BIT}
and LOCATE(#{email,jdbcType=VARCHAR}, users.email)>0
and users.flag_available = #{flagAvailable,jdbcType=BIT}
GROUP BY users.id order by id desc

select
from sys_console_users where id in
#{id}
