mapper.select:
select

from wp_cfg_frame_process_version
where 1=1

  and version = #{version,jdbcType=INTEGER}


  and cfg_frame_process_id = #{cfgFrameProcessId,jdbcType=INTEGER}


  and flag_avaliable = #{flagAvaliable,jdbcType=BIT}


  and create_person = #{createPerson,jdbcType=INTEGER}


  and create_time = #{createTime,jdbcType=TIMESTAMP}


  and update_person = #{updatePerson,jdbcType=INTEGER}


  and update_time = #{updateTime,jdbcType=TIMESTAMP}


  and flag_is_last = #{flagIsLast,jdbcType=BIT}


  and company_user_id = #{companyUserId,jdbcType=INTEGER}


  and company_active_time = #{companyActiveTime,jdbcType=TIMESTAMP}


  and partner_company_user_id = #{partnerCompanyUserId,jdbcType=INTEGER}


  and partner_company_active_time = #{partnerCompanyActiveTime,jdbcType=TIMESTAMP}
