mapper.select:
select

from wp_cfg_process
where 1=1

  and company_id = #{companyId,jdbcType=INTEGER}


  and process_name = #{processName,jdbcType=VARCHAR}


  and description = #{description,jdbcType=VARCHAR}


  and process_code = #{processCode,jdbcType=VARCHAR}


  and flag_avaliable = #{flagAvaliable,jdbcType=BIT}


  and flag_deleted = #{flagDeleted,jdbcType=BIT}


  and create_person = #{createPerson,jdbcType=INTEGER}


  and create_time = #{createTime,jdbcType=TIMESTAMP}


  and update_person = #{updatePerson,jdbcType=INTEGER}


  and update_time = #{updateTime,jdbcType=TIMESTAMP}


  and avaliable_time = #{avaliableTime,jdbcType=TIMESTAMP}
