mapper.select:
select

from wp_frame_process
where 1=1

  and frame_process_cfg_id = #{frameProcessCfgId,jdbcType=INTEGER}


  and frame_process_code = #{frameProcessCode,jdbcType=VARCHAR}


  and version_id = #{versionId,jdbcType=INTEGER}


  and flag_deleted = #{flagDeleted,jdbcType=BIT}


  and create_person = #{createPerson,jdbcType=INTEGER}


  and purchase_create_person = #{purchaseCreatePerson,jdbcType=INTEGER}


  and create_time = #{createTime,jdbcType=TIMESTAMP}


  and frame_process_status = #{frameProcessStatus,jdbcType=VARCHAR}
