mapper.select:
select

,

from wp_process_operate_log
where 1=1

  and frame_process_id = #{frameProcessId,jdbcType=INTEGER}


  and cfg_process_step_id = #{cfgProcessStepId,jdbcType=INTEGER}


  and operate_person = #{operatePerson,jdbcType=INTEGER}


  and operate_time = #{operateTime,jdbcType=TIMESTAMP}


  and photos_url = #{photosUrl,jdbcType=VARCHAR}


  and photos_name = #{photosName,jdbcType=VARCHAR}


  and files_url = #{filesUrl,jdbcType=VARCHAR}


  and files_name = #{filesName,jdbcType=VARCHAR}


  and operate_result = #{operateResult,jdbcType=VARCHAR}


  and time_used = #{timeUsed,jdbcType=INTEGER}


  and flag_timeout = #{flagTimeout,jdbcType=BIT}


  and description = #{description,jdbcType=LONGVARCHAR}

select log.id, log.frame_process_id frameProcessId, log.cfg_process_step_id cfgProcessStepId, log.operate_person operatePerson, log.operate_time operateTime, log.photos_url photosUrl, log.photos_name photosName, log.files_url filesUrl, log.files_name filesName, log.operate_result operateResult, log.time_used timeUsed, log.flag_timeout flagTimeout, log.description, cps.step_name stepName, users.nickname from wp_process_operate_log log left join sys_company_users users on log.operate_person=users.id left join wp_cfg_process_step cps on cps.id=log.cfg_process_step_id where 1=1
and log.frame_process_id = #{frameProcessId,jdbcType=INTEGER}
and log.cfg_process_step_id = #{cfgProcessStepId,jdbcType=INTEGER}
order by log.operate_time desc
