mapper.select:
SELECT id, name, persons,startTime,flagTakeStatus,projectId,flagProject, flagDefault, createTime, createPerson, CASE WHEN COUNT(id)=(SELECT count(1) FROM cfg_region WHERE LEVEL=3 AND region_name is NOT NULL AND region_name!='') or (flagDefault=1 and persons is not null) THEN 0 ELSE 1 END cStatus,companyId,companyName,vendorsId,vendorName,objectType
FROM (select route.id, route.name, route.persons, DATE_FORMAT(route.start_time,'%Y-%c-%d %H:%i:%s') startTime, flag_take_status flagTakeStatus, route.project_id projectId, route.flag_project flagProject, route.flag_default flagDefault, DATE_FORMAT(route.create_time,'%Y-%c-%d %H:%i:%s') createTime, route.create_person createPerson, company.id companyId, company.company_name companyName, vendors.id vendorsId, vendors.vendor_name vendorName, CASE WHEN project.belong_object_type="VENDOR" THEN 0 ELSE 1 END objectType from cfg_dispatch_route route LEFT JOIN cfg_dispatch_route_detail detail ON detail.route_id=route.id LEFT JOIN pjt_project project ON project.id=route.project_id LEFT JOIN dmi_company_info company ON company.id=project.company_id LEFT JOIN dmi_intergration_vendors vendors ON vendors.id=company.vendor_id
) a GROUP BY id desc
