mapper.insert:
insert into sys_company_permission (id, name, url, 
  level, parent_id, order_id, 
  create_time, update_time, flag_deleted
  )
values (#{id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{url,jdbcType=VARCHAR}, 
  #{level,jdbcType=INTEGER}, #{parentId,jdbcType=INTEGER}, #{orderId,jdbcType=INTEGER}, 
  #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{flagDeleted,jdbcType=BIT}
  )

mapper.select:
SELECT permission.id,permission.name, permission.url,permission.level,permission.parent_id parentId, permission.order_id orderId,permission.create_time createTime, permission.update_time updateTime,permission.flag_deleted flagDeleted,api.url apiUrl FROM sys_company_permission permission LEFT JOIN sys_publish_version_permission vpermission ON vpermission.permission_id=permission.id LEFT JOIN sys_publish_version_define vdefine ON vdefine.id=vpermission.version_id LEFT JOIN sys_company_purchase_version pversion ON pversion.version_id=vdefine.id LEFT JOIN sys_company_role_permission rolepermission ON rolepermission.permission_id=vpermission.permission_id LEFT JOIN sys_company_role role ON role.id=rolepermission.role_id LEFT JOIN sys_company_user_role_relation relation ON relation.role_id=role.id LEFT JOIN sys_company_permission_api_relation apiRelation ON apiRelation.permission_id=permission.id LEFT JOIN sys_company_api api ON apiRelation.api_id=api.id WHERE pversion.company_id=#{companyId,jdbcType=INTEGER} AND permission.flag_deleted=0 and rolepermission.flag_deleted=0 AND vpermission.flag_deleted =0 AND vdefine.flag_deleted = 0 AND pversion.flag_deleted=0
AND relation.user_id=#{userId,jdbcType=INTEGER}
and LOCATE(#{name}, permission.name)>0
and LOCATE(#{url}, permission.url)>0
and permission.level = #{level,jdbcType=INTEGER}
and permission.parent_id = #{parentId,jdbcType=INTEGER}
and permission.order_id = #{orderId,jdbcType=INTEGER}
and (TO_DAYS(permission.create_time)=TO_DAYS(#{createTime,jdbcType=TIMESTAMP}))=1
and (TO_DAYS(permission.update_time)=TO_DAYS(#{updateTime,jdbcType=TIMESTAMP}))=1
and permission.flag_deleted = #{flagDeleted,jdbcType=BIT}
group by permission.id order by permission.order_id asc

SELECT permission.id,permission.name, permission.url,permission.level,permission.parent_id parentId, permission.order_id orderId,permission.create_time createTime, permission.update_time updateTime,permission.flag_deleted flagDeleted
,CASE WHEN locate(permission.id,(SELECT GROUP_CONCAT(a.permission_id) FROM (SELECT permission_id FROM sys_company_role_permission WHERE role_id=#{roleId} GROUP BY permission_id) a)) > 0 THEN 1 ELSE 0 END flagSelect
FROM sys_company_permission permission LEFT JOIN sys_publish_version_permission vpermission ON vpermission.permission_id=permission.id LEFT JOIN sys_publish_version_define vdefine ON vdefine.id=vpermission.version_id LEFT JOIN sys_company_purchase_version pversion ON pversion.version_id=vdefine.id WHERE permission.flag_deleted=0 AND pversion.company_id=#{companyId,jdbcType=INTEGER} and vpermission.flag_deleted =0 AND vdefine.flag_deleted = 0 AND pversion.flag_deleted=0
and LOCATE(#{name}, permission.name)>0
and LOCATE(#{url}, permission.url)>0
and permission.level = #{level,jdbcType=INTEGER}
and permission.parent_id = #{parentId,jdbcType=INTEGER}
and permission.order_id = #{orderId,jdbcType=INTEGER}
and (TO_DAYS(permission.create_time)=TO_DAYS(#{createTime,jdbcType=TIMESTAMP}))=1
and (TO_DAYS(permission.update_time)=TO_DAYS(#{updateTime,jdbcType=TIMESTAMP}))=1
and permission.flag_deleted = #{flagDeleted,jdbcType=BIT}
group by permission.id order by permission.order_id asc

select

from sys_company_permission
where 1=1

   and LOCATE(#{name}, name)>0


    and LOCATE(#{url}, url)>0


    and level = #{level,jdbcType=INTEGER}


    and  parent_id = #{parentId,jdbcType=INTEGER}


    and  order_id = #{orderId,jdbcType=INTEGER}


    and (TO_DAYS(create_time)=TO_DAYS(#{createTime,jdbcType=TIMESTAMP}))=1


    and (TO_DAYS(update_time)=TO_DAYS(#{updateTime,jdbcType=TIMESTAMP}))=1


    and flag_deleted = #{flagDeleted,jdbcType=BIT}
