mapper.select:
select

from bif_building
where flag_deleted = 0
AND longitude <= #{longitudeMax,jdbcType=DECIMAL}
AND longitude >= #{longitudeMin,jdbcType=DECIMAL}
AND latitude <= #{latitudeMax,jdbcType=DECIMAL}
AND latitude >= #{latitudeMin,jdbcType=DECIMAL}

select distinct building.flag_import AS flagImport, building.id AS id, building.longitude AS longitude, building.latitude AS latitude, building.province AS province, building.city AS city, building.district AS district, building.building_name AS buildingName, building.apply_id AS applyId, building.create_time AS createTime, users.username AS createPerson, building.update_time AS updateTime, address.road AS road, address.id AS addressId from bif_building building inner join bif_building_address address ON address.building_id = building.id and address.flag_major = 1 and address.flag_deleted = 0 LEFT JOIN sys_company_users users ON building.create_person = users.id where building.flag_deleted = 0
and building.province = #{province,jdbcType=VARCHAR}
and building.city = #{city,jdbcType=VARCHAR}
and building.district = #{district,jdbcType=VARCHAR}
and building.flag_import = #{flagImport,jdbcType=BIT}
and building.flag_amend = #{flagAmend,jdbcType=BIT}

select distinct building.id AS id, building.longitude AS longitude, building.latitude AS latitude, building.province AS province, building.city AS city, building.district AS district, building.building_name AS buildingName, building.apply_id AS applyId, building.create_time AS createTime, users.username AS createPerson, address.road AS road, address.id AS addressId from bif_building building inner join bif_building_address address ON address.building_id = building.id and address.flag_deleted = 0 LEFT JOIN sys_company_users users ON building.create_person = users.id where building.flag_deleted = 0
and building.province = #{province,jdbcType=VARCHAR}
and building.city = #{city,jdbcType=VARCHAR}
and building.district = #{district,jdbcType=VARCHAR}
and (building.building_name like '%${buildingName}%' or address.road like '%${buildingName}%')
