mapper.select:
SELECT

FROM
cfg_dispatch_model_config
WHERE
flag_deleted = 0
AND id != #{id,jdbcType=INTEGER}

    AND company_id = #{companyId,jdbcType=INTEGER}


    AND model_name = #{modelName,jdbcType=VARCHAR}

SELECT model.id, model.flag_avaliable flagAvaliable, comp.company_name companyName, pro.project_name projectName, model.model_name modelName, model.create_time createTime, suer.username createPerson FROM cfg_dispatch_model_config model LEFT JOIN sys_company_users suer on model.create_person = suer.id LEFT JOIN dmi_company_info comp on comp.id = model.company_id LEFT JOIN pjt_project pro ON pro.id = model.project_id WHERE 1=1 AND model.flag_deleted = 0
AND model.company_id = #{companyId,jdbcType=INTEGER}
AND model.project_id = #{projectId,jdbcType=INTEGER}
AND model.create_person = #{createPerson,jdbcType=INTEGER}
AND model.create_time >= #{createTimeStart,jdbcType=TIMESTAMP}
AND model.create_time <= DATE_ADD(#{createTimeEnd,jdbcType=TIMESTAMP} , INTERVAL 1 DAY)
ORDER BY model.create_time DESC

SELECT CONCAT( pro.project_name, '--', cdmc.model_name ) projectModelName, cdmc.id, cdmc.company_id companyId, cdmc.project_id projectId, cdmc.dispatch_model_id dispatchModelId, cdmc.belong_object_type belongObjectType, cdmc.belong_object_id belongObjectId, cdmc.equipment_request equipmentRequest, cdmc.other_request otherRequest, cdmc.dispatch_description dispatch_description, cdmc.backup_device_request backupDeviceRequest, cdmc.suggest_price suggestPrice, cdmc.suggest_unit suggestUnit, cdmc.flag_fixed_price flagFixedPrice, cdmc.flag_deleted flagDeleted, cdmc.flag_avaliable flagAvaliable, cdmc.create_time createTime, cdmc.update_person updatePerson, cdmc.update_time updateTime, cdmc.upload_file_name uploadFileName, cdmc.upload_file_url uploadFileUrl, cdmc.flag_auto_commit flagAutoCommit, cdmc.complete_profile_template_name completeProfileTemplateName, cdmc.complete_profile_template_url completeProfileTemplateUrl FROM pjt_project pro LEFT JOIN cfg_dispatch_model_config cdmc ON pro.id = cdmc.project_id LEFT JOIN sys_company_users suer ON pro.create_person = suer.id LEFT JOIN dmi_company_info comp ON comp.id = pro.company_id WHERE 1=1
AND cdmc.company_id = #{companyId,jdbcType=INTEGER}
AND pro.flag_deleted = 0 AND pro.flag_available = 1 AND cdmc.flag_avaliable = 1 ORDER BY cdmc.create_time DESC
