mapper.select:
select contact.id, contact.contact_name contactName, contact.contact_name_cn contactNameCn, contact.contact_name_en contactNameEn, contact.telephone, contact.mobile, contact.account, contact.memo, contact.qq, contact.wechat, contact.flag_available flagAvailable, contact.belong_company_id belongCompanyId, contact.create_time createTime, contact.create_person createPerson, contact.update_time updateTime, contact.update_person updatePerson, contact.email, contact.source_type sourceType, contact.store_id storeId, contact.password, contact.flag_major flagMajor from dmi_contact contact where contact.store_id = #{storeId,jdbcType=INTEGER}
AND contact.contact_name = #{contactName,jdbcType=VARCHAR}
AND contact.mobile = #{mobile,jdbcType=VARCHAR}
AND contact.telephone = #{telephone,jdbcType=VARCHAR}
AND contact.email = #{email,jdbcType=VARCHAR}
AND contact.wechat = #{wechat,jdbcType=VARCHAR}
AND contact.qq = #{qq,jdbcType=VARCHAR}
and contact.flag_deleted = 0 order by contact.update_time desc

        select
        
        from dmi_contact

        where password = #{oldPassword,jdbcType=VARCHAR}

        and id = #{id,jdbcType=INTEGER}
        and flag_deleted = 0

mapper.update:
update dmi_contact
password = #{newPassword,jdbcType=VARCHAR},
where 1=1 and id = #{id,jdbcType=INTEGER}

mapper.select:
select

from dmi_contact
where 1=1

    and contact_name = #{contactName,jdbcType=VARCHAR}


    and mobile = #{mobile,jdbcType=VARCHAR}


    and store_id = #{storeId,jdbcType=VARCHAR}

and flag_deleted = 0

SELECT ( CASE
WHEN ds.store_name IS NULL THEN ( CASE WHEN ( SELECT company_id FROM dmi_contact_company_relation WHERE contact_id = dc.id ) IS NOT NULL THEN dci.company_name END ) ELSE ds.store_name END ) AS StoreOrCompany, dci.company_name companyName, dc.contact_name contactName, dc.contact_name_cn contactNameCn, dc.contact_name_en contactNameEn, dc.telephone, dc.mobile, dc.qq, dc.wechat, dc.email, dc.memo FROM dmi_contact dc LEFT JOIN dmi_company_info dci ON dci.id = dc.belong_company_id LEFT JOIN dmi_contact_store_relation dcsr ON dcsr.contact_id = dc.id LEFT JOIN dmi_store ds ON ds.id = dcsr.store_id WHERE belong_company_id = #{belongCompanyId,jdbcType=INTEGER}
AND dc.contact_name = #{contactName,jdbcType=VARCHAR}
AND dc.mobile = #{mobile,jdbcType=VARCHAR}
AND dc.telephone = #{telephone,jdbcType=VARCHAR}
AND dc.email = #{email,jdbcType=VARCHAR}
AND dc.wechat = #{wechat,jdbcType=VARCHAR}
AND dc.qq = #{qq,jdbcType=VARCHAR}
AND dc.flag_deleted = 0

mapper.update:
update dmi_contact
set flag_major = 0
where 1=1

    and store_id = #{storeId,jdbcType=VARCHAR}


    and belong_company_id = #{belongCompanyId,jdbcType=INTEGER}

and id != #{id,jdbcType=INTEGER}
