mapper.select:
SELECT pro.id, comp.company_name companyName, pro.project_name projectName, pro.project_code projectCode, pro.create_time createTime, pro.update_time updateTime, suer.username createPerson FROM pjt_project pro LEFT JOIN sys_company_users suer on pro.create_person = suer.id LEFT JOIN dmi_company_info comp on comp.id = pro.company_id WHERE 1=1
AND pro.company_id = #{companyId,jdbcType=INTEGER}
AND pro.create_time >= #{createTimeStart,jdbcType=TIMESTAMP}
AND pro.create_time <= DATE_ADD(#{createTimeEnd,jdbcType=TIMESTAMP} , INTERVAL 1 DAY)
AND pro.flag_deleted = 0 AND pro.flag_available = 1 ORDER BY pro.create_time DESC

SELECT
FROM pjt_project WHERE flag_deleted = 0 AND flag_available = 1
AND id != #{id,jdbcType=INTEGER}
AND company_id = #{companyId,jdbcType=INTEGER}
AND (project_name = #{projectName,jdbcType=VARCHAR} OR project_code = #{projectCode,jdbcType=VARCHAR})

SELECT
from pjt_project WHERE flag_deleted = 0 AND flag_available = 1 AND company_id = #{id}
AND belong_object_type="VENDOR"
and id not in(SELECT project_id from cfg_dispatch_route WHERE 1=1 /*flag_default = 0*/
and project_id!=(SELECT CASE WHEN COUNT(1) = 0 THEN 0 ELSE id END id from pjt_project WHERE flag_deleted = 0 AND flag_available = 1 AND company_id = #{id} AND id = #{projectId}
AND belong_object_type="VENDOR"
)
)

SELECT pro.id, comp.company_name companyName, pro.project_name projectName, pro.project_code projectCode, pro.create_time createTime, pro.update_time updateTime, suer.username createPerson, pro.flag_available flagAvailable FROM pjt_project pro LEFT JOIN sys_company_users suer on pro.create_person = suer.id LEFT JOIN dmi_company_info comp on comp.id = pro.company_id WHERE 1=1
AND pro.company_id = #{companyId,jdbcType=INTEGER}
AND pro.create_time >= #{createTimeStart,jdbcType=TIMESTAMP}
AND pro.create_time <= DATE_ADD(#{createTimeEnd,jdbcType=TIMESTAMP} , INTERVAL 1 DAY)
AND pro.flag_deleted = 0 ORDER BY pro.update_time DESC

SELECT pro.id, comp.company_name companyName, pro.project_name projectName, pro.project_code projectCode, pro.create_time createTime, pro.update_time updateTime, suer.username createPerson, pro.flag_available flagAvailable, pro.flag_default flagDefault FROM pjt_project pro LEFT JOIN sys_company_users suer on pro.create_person = suer.id LEFT JOIN dmi_company_info comp on comp.id = pro.company_id WHERE 1=1
AND pro.company_id = #{companyId,jdbcType=INTEGER}
AND pro.create_time >= #{createTimeStart,jdbcType=TIMESTAMP}
AND pro.create_time <= DATE_ADD(#{createTimeEnd,jdbcType=TIMESTAMP} , INTERVAL 1 DAY)
AND pro.flag_deleted = 0 AND pro.flag_available = 1 ORDER BY pro.create_time DESC

select

from pjt_project
where 1 = 1

    and id = #{id,jdbcType=INTEGER}


    and company_id = #{companyId,jdbcType=INTEGER}


    and project_name = #{projectName,jdbcType=VARCHAR}


    and project_code = #{projectCode,jdbcType=VARCHAR}


    and flag_available = #{flagAvailable,jdbcType=BIT}


    and flag_deleted = #{flagDeleted,jdbcType=BIT}


    and belong_object_type = #{belongObjectType,jdbcType=VARCHAR}


    and belong_object_id = #{belongObjectId,jdbcType=INTEGER}


    and create_person = #{createPerson,jdbcType=INTEGER}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and update_person = #{updatePerson,jdbcType=INTEGER}


    and update_time = #{updateTime,jdbcType=TIMESTAMP}


    and flag_default = #{flagDefault,jdbcType=BIT}
