mapper.insert:
insert into sys_company_user_role_relation (id, company_id, user_id, 
  role_id, create_time, create_person, 
  update_time, update_person, flag_deleted
  )
values (#{id,jdbcType=INTEGER}, #{companyId,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, 
  #{roleId,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, #{createPerson,jdbcType=INTEGER}, 
  #{updateTime,jdbcType=TIMESTAMP}, #{updatePerson,jdbcType=INTEGER}, #{flagDeleted,jdbcType=BIT}
  )

mapper.select:
select

from sys_company_user_role_relation
where 1=1

  and company_id = #{companyId,jdbcType=INTEGER}


  and user_id = #{userId,jdbcType=INTEGER}


  and role_id = #{roleId,jdbcType=INTEGER}


  and create_time = #{createTime,jdbcType=TIMESTAMP}


  and create_person = #{createPerson,jdbcType=INTEGER}


  and update_time = #{updateTime,jdbcType=TIMESTAMP}


  and update_person = #{updatePerson,jdbcType=INTEGER}


  and flag_deleted = #{flagDeleted,jdbcType=BIT}

select
scurr.id, scurr.company_id companyId, scurr.user_id userId,
scurr.role_id roleId, scurr.create_time createTime,
scurr.create_person createPerson, scurr.update_time updateTime, scurr.update_person updatePerson,
scurr.flag_deleted flagDeleted,scr.role_name roleName
from sys_company_user_role_relation scurr
left join sys_company_role scr on scurr.role_id =scr.id
where 1=1

  and scurr.company_id = #{companyId,jdbcType=INTEGER}


  and scurr.user_id = #{userId,jdbcType=INTEGER}


  and scurr.role_id = #{roleId,jdbcType=INTEGER}


  and scurr.create_time = #{createTime,jdbcType=TIMESTAMP}


  and scurr.create_person = #{createPerson,jdbcType=INTEGER}


  and scurr.update_time = #{updateTime,jdbcType=TIMESTAMP}


  and scurr.update_person = #{updatePerson,jdbcType=INTEGER}


  and scurr.flag_deleted = #{flagDeleted,jdbcType=BIT}

mapper.delete:
delete from sys_company_user_role_relation
where company_id = #{companyId,jdbcType=INTEGER}
and user_id = #{userId,jdbcType=INTEGER}
